package com.sun.xiaotian.demo.algorithm.leetcode;


import java.util.HashSet;
import java.util.Set;

/**
 * <a href="https://leetcode.com/problems/unique-substrings-in-wraparound-string/"> 循环字符串<a/>
 */
public class UniqueSubstringsInWraparoundString_467 {

    /**
     * 方法一 运行时间和内存超限
     *
     * @param p
     * @return
     */
    public int findSubstringInWraproundString(String p) {
        if (p.length() <= 1) {
            return p.length();
        }
        Set<String> resultSet = new HashSet<>();

        //分割字段
        int start = 0;
        for (int i = 1; i < p.length(); i++) {
            if (!(p.charAt(i) == p.charAt(i - 1) + 1 || (p.charAt(i) == 'a' && p.charAt(i - 1) == 'z'))) {
                subStr(p.substring(start, i), resultSet);
                start = i;
            }
        }
        subStr(p.substring(start), resultSet);
        return resultSet.size();
    }

    /**
     * 方法二来自网络
     *
     * @param str
     * @param resultSet
     */
    private void subStr(String str, Set<String> resultSet) {
        if (resultSet.contains(str)) {
            return;
        }
        resultSet.add(str);
        if (str.length() == 1) {
            return;
        }
        subStr(str.substring(0, str.length() - 1), resultSet);
        subStr(str.substring(1), resultSet);
    }

    public int findSubstringInWraproundString1(String p) {
        int[] count = new int[26];
        int curLen = 1;
        int strLength = p.length();
        for (int i = strLength - 1; i >= 0; i--) {
            if (i < strLength - 1 && (p.charAt(i) == p.charAt(i + 1) - 1 || p.charAt(i) == p.charAt(i + 1) + 25)) {
                curLen++;
            } else {
                curLen = 1;
            }
            count[p.charAt(i) - 'a'] = Math.max(count[p.charAt(i) - 'a'], curLen);
        }

        int result = 0;
        for (int c : count) {
            result += c;
        }
        return result;
    }

    public static void main(String[] args) {
        UniqueSubstringsInWraparoundString_467 uniqueSubstringsInWraparoundString_467 = new UniqueSubstringsInWraparoundString_467();
        System.out.println(uniqueSubstringsInWraparoundString_467.findSubstringInWraproundString1("abcdefghijklmnopqrstuvwxyz"));
        System.out.println(uniqueSubstringsInWraparoundString_467.findSubstringInWraproundString1("zab"));
        System.out.println(uniqueSubstringsInWraparoundString_467.findSubstringInWraproundString1("cac"));
        System.out.println(uniqueSubstringsInWraparoundString_467.findSubstringInWraproundString1(""));
        System.out.println(uniqueSubstringsInWraparoundString_467.findSubstringInWraproundString1(""));
    }
}
